console.log(defaultApp.name);  // "[DEFAULT]"
```

### Example 2: Initializing a Named Firebase App

```typescript
import { initializeApp, getApp } from 'firebase/app';

const otherAppConfig = {
  databaseURL: "https://<OTHER_DATABASE_NAME>.firebaseio.com",
  storageBucket: "<OTHER_STORAGE_BUCKET>.appspot.com"
};

// Initialize another app
const otherApp = initializeApp(otherAppConfig, "otherApp");

// Retrieve the named app
const retrievedOtherApp = getApp("otherApp");
console.log(retrievedOtherApp.name);  // "otherApp"
```

### Example 3: Deleting a Firebase App

```typescript
import { initializeApp, deleteApp } from 'firebase/app';

const firebaseConfig = {
  apiKey: "AIza....",
  authDomain: "YOUR_APP.firebaseapp.com",
  databaseURL: "https://YOUR_APP.firebaseio.com",
  storageBucket: "YOUR_APP.appspot.com",
  messagingSenderId: "123456789",
  appId: "1:123456789:web:abcdefg12345"
};

// Initialize the default app
const app = initializeApp(firebaseConfig);

// Delete the app
deleteApp(app)
  .then(() => {
    console.log("App deleted successfully");
  })
  .catch((error) => {
    console.log("Error deleting app:", error);
  });
```

### Example 4: Setting Log Level and Log Handler

```typescript
import { setLogLevel, onLog, LogLevelString } from 'firebase/app';

// Set log level to 'debug'
setLogLevel('debug' as LogLevelString);

// Set a custom log handler
onLog((logEntry) => {
  console.log(`[${logEntry.level}] ${logEntry.message}`);
});
```

### Example 5: Registering a Library Version

```typescript
import { registerVersion } from 'firebase/app';

// Register a library version
registerVersion('my-custom-library', '1.0.0', 'node');
```

### Example 6: Initializing a Firebase Server App

```typescript
import { initializeServerApp } from 'firebase/app';

const serverAppConfig = {
  apiKey: "AIza....",
