import { getAuth, multiFactor, PhoneAuthProvider, PhoneMultiFactorGenerator } from 'firebase/auth';

const auth = getAuth();

async function enrollMFA(phoneNumber: string, appVerifier: any) {
  const user = auth.currentUser;
  if (!user) {
    console.error('No user signed in');
    return;
  }

  const multiFactorUser = multiFactor(user);
  const session = await multiFactorUser.getSession();

  const phoneAuthProvider = new PhoneAuthProvider(auth);
  const verificationId = await phoneAuthProvider.verifyPhoneNumber({ phoneNumber, session }, appVerifier);

  // Obtain verification code from user
  const verificationCode = prompt('Enter the verification code you received');

  const phoneCredential = PhoneAuthProvider.credential(verificationId, verificationCode);
  const assertion = PhoneMultiFactorGenerator.assertion(phoneCredential);

  try {
    await multiFactorUser.enroll(assertion, 'My Phone Number');
    console.log('MFA enrollment successful');
  } catch (error) {
    console.error('Error enrolling MFA:', error);
  }
}

// Assume appVerifier is already initialized
const appVerifier = ...;
enrollMFA('+1234567890', appVerifier);
