### Example 4: Handling Function Calls in Responses

```typescript
import { getVertexAI, getGenerativeModel, VertexAI, GenerativeModel, GenerateContentRequest, GenerateContentResult, FunctionDeclaration, FunctionResponse } from 'firebase';

// Initialize Firebase app (assuming you have already set up Firebase)
import { initializeApp } from 'firebase/app';
const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_AUTH_DOMAIN",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_STORAGE_BUCKET",
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
  appId: "YOUR_APP_ID"
};
const app = initializeApp(firebaseConfig);

// Initialize Vertex AI
const vertexAI: VertexAI = getVertexAI(app, { location: 'us-central1' });

// Initialize Generative Model
const modelParams = {
  model: 'text-davinci-003',
  generationConfig: {
    maxOutputTokens: 100,
    temperature: 0.7
  },
  tools: [{
    functionDeclarations: [{
      name: 'getWeather',
      description: 'Fetches the current weather for a given location',
      parameters: {
        type: 'object',
        properties: {
          location: { type: 'string', description: 'The location to fetch weather for' }
        },
        required: ['location']
      }
    }]
  }]
};
const generativeModel: GenerativeModel = getGenerativeModel(vertexAI, modelParams);

// Generate content with function call
const request: GenerateContentRequest = {
  contents: [{ role: 'user', parts: [{ text: 'What is the weather like in New York?' }] }]
};

generativeModel.generateContent(request).then((result: GenerateContentResult) => {
  const functionCalls = result.response.functionCalls();
  if (functionCalls) {
    functionCalls.forEach((funcCall) => {
      if (funcCall.name === 'getWeather') {
        // Handle the function call, e.g., fetch weather data from an API
        const weatherData = { temperature: '20C', condition: 'Sunny' };
        const functionResponse: FunctionResponse = {
          name: 'getWeather',
          response: weatherData
        };

        // Send the function response back to the model
        const responseRequest: GenerateContentRequest = {
          contents: [{ role: 'function', parts: [{ functionResponse }] }]
        };

        generativeModel.generateContent(responseRequest).then((finalResult: GenerateContentResult) => {
          console.log(finalResult.response.text());
        });
      }
    });
  }
}).catch((error) => {
  console.error('Error generating content:', error);
});
